import React from "react"


//function to set the duration of the break time
//default to 5 minutes, no less than 1 or more than 15).
export default function FocusDuration({disableDurationChange, setDisableDurationChange, FocusDuration, setFocusDuration}) {
    function handleIncrease () {
        setFocusDuration((FocusDuration) => FocusDuration + 5)
    }
    function handleDecrease() {
        setFocusDuration((FocusDuration) => FocusDuration - 5)
    }
    return(<>
            <div className="input-group-append">
              {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
                onClick={handleDecrease}
                disabled={disableDurationChange}
              >
                <span className="oi oi-minus" />
              </button>
              {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
                onClick={handleIncrease}
                disabled={disableDurationChange}
              >
                <span className="oi oi-plus" />
              </button>
            </div>
    </>)
}
