import React from "react";

export default function ProgressBar({focusDuration, session, isTimerRunning }){
    // divide total time by 100 = time elapsed
    // 1 tick = total/100
    // increase width by 1 for time elapsed
    // pause if paused and run if started
    // stop and reset if stopped
    //is timer running 
    if(isTimerRunning){
        console.log(session.timeRemaining)
    }


    return (<>
        <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: "100%"}} // TODO: Increase width % as elapsed time increases
              />
            </div>
            </>)
}
