import React from "react";

export default function ProgressBar({focusDuration, session, isTimerRunning }){
    // divide total time by 100 = time elapsed
    // 1 tick = total/100
    // increase width by 1 for time elapsed
    // pause if paused and run if started
    // stop and reset if stopped
    //is timer running
    //let deltaAria = 0;
    let progress = 0;
    let width = 0;
    width += progress;

    const maxDuration = focusDuration*60;
    if(isTimerRunning){
        progress = ((maxDuration - session.timeRemaining)/maxDuration)*100; 
        console.log(progress)
        //console.log(deltaAria);
        //const delta = session.timeRemaining/(focusDuration*60);
        //progress += delta;
        //console.log(session.timeRemaining);
    }


    return (<>
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={progress} // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: width+"%"}} // TODO: Increase width % as elapsed time increases
              />
            </div>
            </>)
}
