import React from "react";
/** 
*@param disable


*/
export default function StopButton({disable, setDisable, setSession, setIsTimerRunning, setDisableDurationChange}){

  //TODO: prop type checking here 

    function handleStop(event) {
    //setting states for disable, session, and timer running in order to reset the timer <- *currently working*  
    //play button function sets disable to false
      setDisable(true);
      setSession(null);
      setIsTimerRunning(false);
      setDisableDurationChange(false);
    };

    //TODO: make sure class implementation needs to be used or not
    return (<>

    <button
      type="button"
      className="btn btn-secondary"
      data-testid="stop"
      title="Stop the session"
      onClick={handleStop}
      disabled={disable}
    >
      <span className="oi oi-media-stop" />
    </button>
</>)
    }
