import React, { useState } from "react";

export default function StopButton({setSession, setIsTimerRunning}){
    const [disable, setDisable] = useState(false);


    function handleStop(event) {
      setDisable(true);
      setSession ;
      setIsTimerRunning;
    };

    return (<>

    <button
      type="button"
      className="btn btn-secondary"
      data-testid="stop"
      title="Stop the session"
      onClick={handleStop}
      disabled={disable}
    >
      <span className="oi oi-media-stop" />
    </button>
</>)
    }
