import React from "react";
import {minutesToDuration, secondsToDuration} from "../utils/duration";

function displayPause(isTimerRunning) {
    return isTimerRunning? null : (<><h3>Paused</h3></>)    

}


export default function ProgressBar({focusDuration, session, isTimerRunning }){

    let progress = 0;

    const maxDuration = focusDuration*60;
    if(isTimerRunning){
        const progress = ((maxDuration - session.timeRemaining)/maxDuration)*100; 
        console.log(progress)
        console.log(session)}

    if (session !== null){
    return (<>

        <div>
        {/* TODO: This area should show only when there is an active focus or break - i.e. the session is running or is paused */}
        <div className="row mb-2">
          <div className="col">
            {/* TODO: Update message below to include current session (Focusing or On Break) total duration */}
            <h2 data-testid="session-title">
                {session?.label} for { minutesToDuration(focusDuration)} minutes
            </h2>
            {/* TODO: Update message below correctly format the time remaining in the current session */}
            <p className="lead" data-testid="session-sub-title">
            {secondsToDuration(session?.timeRemaining)} remaining
            </p>
            <div className="pause" >{displayPause(isTimerRunning)}</div>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={progress} // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: progress+"%"}} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>    
        </>)
    } else {
        console.log(session)
        return null;
    }
}
